package 스레드;

public class 자동차경주게임1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 10; i++) {
			System.out.println("※");
		}
		for (int i = 0; i < 10; i++) {
			System.out.println("★");
		}
		for (int i = 0; i < 10; i++) {
			System.out.println("◆");
		}
	}

}
